import React, { useState } from 'react';
import Button from '@mui/material/Button';
import axios from 'axios';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const ViewForm = () => {
    const path = 'http://localhost:5001'
    const [books, setBooks] = useState([]);
    const [filters, setfilters] = useState({
        title: "",
        author: "",
        genre: ""
    });

    const handleChange = (e) => {
        const {name, value} = e.target
        setfilters({
            ...filters,
            [name]: value
        })
    }

    const getAll = async () => {
        try {
            const response = await axios.get(`${path}/allBooks`);
            setBooks(response.data);
        } catch (error) {
            console.error('Error fetching books:', error);
        }
    };
    const handleClick = async () => {
        try {
            const response = await axios.get(`${path}/books`); // Adjust the URL as needed
            setBooks(response.data);
        } catch (error) {
            console.error('Error fetching books:', error);
        }
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Submitted", filters)
    };

    return (
        <div>
            <Box
                component='form'
                autoComplete='off'
                noValidate
                onSubmit={handleSubmit}
            >
                <TextField name='title' label='Title' onChange={handleChange} variant='outlined' />
                <TextField name='author' label='Author' onChange={handleChange} variant='outlined' />
                <TextField name='genre' label='Genre' onChange={handleChange} variant='outlined' />
                <Button type='submit'>
                    View Books
                </Button>
            </Box>
            <Button onClick={getAll}>
                View all Books
            </Button>
            <Paper sx={{overflow: 'hidden'}}>
                <TableContainer component={Paper} sx={{maxHeight: 400}}>
                    <Table stickyHeader>
                        <TableHead>
                            <TableRow>
                                <TableCell>Title</TableCell>
                                <TableCell align='right'>Author</TableCell>
                                <TableCell align='right'>Genre</TableCell>
                                <TableCell align='right'>Publication Date</TableCell>
                                <TableCell align='right'>ISBN</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {books.map((book)=> (
                                <TableRow key={book.id}>
                                    <TableCell component="th" scope="row">
                                        {book.title}
                                    </TableCell>
                                    <TableCell align="right">{book.author}</TableCell>
                                    <TableCell align="right">{book.genre}</TableCell>
                                    <TableCell align="right">{book.publication_date.slice(0,10)}</TableCell>
                                    <TableCell align="right">{book.isbn}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </div>
    );
};

export default ViewForm;
